frappe.ui.form.on("Trip Details", "supplier_rate", function(frm, cdt, cdn)
{
    var row = locals[cdt][cdn];
    frappe.model.set_value(cdt, cdn, "supplier_amount", row.supplier_quantity * row.supplier_rate);
});

